import Link from "next/link";
import { Component } from "react";
import {connect} from 'react-redux';
import Image from '../static/Image'

class Result extends Component{

    constructor(props){
        super(props);
    }

    //条件に応じてレンダリング内容を変える.
    renderWithCondition(num_correct) {
        if (num_correct < 4) {
            return (
            <div className="result-image-box">
                <Image fname="taisi.jpg" size="400"/>
            </div>)
            } else {
            return(
            <div className="result-image-box">
                <Image fname="tuguko.jpg" size="400"/>
            </div>)
            }
        }

    render(){
        return(
            <div className="result">
                あなたの正解数は{this.props.correct}問です。
                {this.renderWithCondition(this.props.question[this.props.q_no[this.props.i]].options[0])}
                <Link href="/">
                    <div className="return_btn" >再挑戦</div>
                </Link>
            </div>
        )
    }
}

Result = connect((state)=>state)(Result)
export default Result;
