import Link from "next/link";
import { Component } from "react";
import {connect} from 'react-redux';
import Image from '../static/Image'

class Result extends Component{

    constructor(props){
        super(props);
        this.state = {isModalOpen: false};
    }

    handleClickOpen(){
        this.setState({isModalOpen: true});
      }

    handleClickClose(){
        this.setState({isModalOpen: false});
      }

    // 正解数に応じてレンダリング内容を変える.(テキスト)
    renderRank(num_correct) {
        if (num_correct < 3) {
            return (
            <div className="result-text-box">
                <span>鬼殺隊隊士級</span>
            </div>)
            } else if(num_correct < 5){
            return(
            <div className="result-text-box">
                <span>継子級</span>
            </div>)
            } else if(num_correct < 7){
            return(
            <div className="result-text-box">
                <span>下弦の鬼級</span>
            </div>)
            } else if(num_correct < 9){
            return(
            <div className="result-text-box">
                <span>柱級</span>
            </div>)
            } else if(num_correct < 10){
            return(
            <div className="result-text-box">
                <span>上弦の鬼級</span>
            </div>)
            } else if(num_correct === 10){
            return(
            <div className="result-text-box">
                <span>無惨級</span>
            </div>)
            }
        }

    // 正解数に応じてレンダリング内容を変える.(図)
    renderHierarchie(num_correct) {
        if (num_correct < 3) {
            return (
            <div className="result-image-box">
                <Image fname="taisi.jpg" size="400"/>
            </div>)
            } else if(num_correct < 5){
            return(
            <div className="result-image-box">
                <Image fname="tuguko.jpg" size="400"/>
            </div>)
            } else if(num_correct < 7){
                return(
                <div className="result-image-box">
                    <Image fname="kagen.jpg" size="400"/>
                </div>)
            } else if(num_correct < 9){
                return(
                <div className="result-image-box">
                    <Image fname="hasira.jpg" size="400"/>
                </div>)
            } else if(num_correct < 10){
                return(
                <div className="result-image-box">
                    <Image fname="jougen.jpg" size="400"/>
                </div>)
            } else if(num_correct === 10){
                return(
                <div className="result-image-box">
                    <Image fname="muzan.jpg" size="400"/>
                </div>)
            }
        }

    render(){
        var ans_list = [];
        for(let i = 0; i < 11; i++){
            ans_list.push(
                <div className="ans_set">
                    <span>{this.props.question[this.props.q_no[i]].q_sentence}:</span>
                    <span>{this.props.question[this.props.q_no[0]].options[this.props.question[this.props.q_no[0]].answer-1]}</span>
                </div>
                );
        }

        let modal;
        if (this.state.isModalOpen){
            modal = (
                <div className='modal'>
                  <div className='modal-inner'>
                    <div className="modal-content">
                       {ans_list}
                    </div>
                    <button className='modal-close-btn' onClick={() => this.handleClickClose()}>
                      とじる
                    </button>
                  </div>
                </div>
              );
          }
        return(
            <div className="result">
                あなたの正解数は{this.props.correct}問です。
                あなたの鬼滅力は・・・
                {this.renderRank(this.props.correct)}
                {this.renderHierarchie(this.props.correct)}
                <div className="print_answer" >
                    <span onClick={() => {this.handleClickOpen()}}>解答をみる</span>
                </div>
                {modal}
                <Link href="/">
                    <div className="retry_btn" >再挑戦</div>
                </Link>
            </div>
        )
    }
}

Result = connect((state)=>state)(Result)
export default Result;
