import React, {Component} from 'react';
import {connect} from 'react-redux';

class Choice extends Component{

    constructor(props){
        super(props);
        this.doAction = this.doAction.bind(this)
    }

    doAction(e){
        if(this.ans == this.question[this.props.i].answer){
            return this.props.dispatch({ type: 'CORRECT'});
        } else {
            return this.props.dispatch({ type: 'INCORRECT'});
        }
    }

    render(){
        return (
            <div className="options">
                <label　className="option" onClick={this.doAction}>
                    <input type="radio" name="options" /><span>{this.props.options[0]}</span>
                </label>
                <label className="option" onClick={this.doAction}>
                    <input type="radio" name="options" /><span>{this.props.options[1]}</span>
                </label>
                <label className="option" onClick={this.doAction}>
                    <input type="radio" name="options" /><span>{this.props.options[2]}</span>
                </label>
            </div>
        );
    }
}

Choice = connect((state)=>state)(Choice)
export default Choice;
