import React, {Component} from 'react';
import {connect} from 'react-redux';

class Choice extends Component{

    constructor(props){
        super(props);
        this.doAction = this.doAction.bind(this);
    }

    doAction(ans_no){
        //ストアに回答データを登録
        if(ans_no== this.props.question[this.props.q_no[this.props.i]].answer){
            this.props.dispatch({ type: 'CORRECT'});
        } else {
            this.props.dispatch({ type: 'INCORRECT'});
        }
        //最終問題の場合結果画面へ遷移
        if(this.props.i===9){
            this.flg = false;
        }
    }

    render(){
        return (
            <div className="options">
                <label　className="option" onClick={this.doAction(1)}>
                    <span>{this.props.question[this.props.q_no[this.props.i]].options[0]}<br/></span>
                </label>
                <label className="option" onClick={this.doAction(2)}>
                    <span>{this.props.question[this.props.q_no[this.props.i]].options[1]}<br/></span>
                </label>
                <label className="option" onClick={this.doAction(3)}>
                    <span>{this.props.question[this.props.q_no[this.props.i]].options[2]}<br/></span>
                </label>
            </div>

        );
    }
}

Choice = connect((state)=>state)(Choice)
export default Choice;
