import React, {Component} from 'react';
import {connect} from 'react-redux';

class Choice extends Component{

    constructor(props){
        super(props);
        this.selectUpdate = this.selectUpdate.bind(this)
    }

    selectUpdate(ans){
        if(ans==1){
            return this.props.dispatch({ type: 'UPDATE1'});
        }else if(ans==2){
            return this.props.dispatch({ type: 'UPDATE2'});
        }else if(ans==3){
            return this.props.dispatch({ type: 'UPDATE3'});
        }
    }

    render(){
        return (
            <div className="options">
                <label　className="option1" onClick={this.selectUpdate(1)}>
                    <input type="radio" name="options" /><span>{this.props.options[0]}</span>
                </label>
                <label className="option2" onClick={this.selectUpdate(2)}>
                    <input type="radio" name="options" /><span>{this.props.options[1]}</span>
                </label>
                <label className="option3" onClick={this.selectUpdate(3)}>
                    <input type="radio" name="options" /><span>{this.props.options[2]}</span>
                </label>
            </div>
        );
    }
}

Choice = connect((state)=>state)(Choice)
export default Choice;
