import React, {Component} from 'react';

class Choice extends Component{

    constructor(props){
        super(props);
        this.fname = "./static/choices/" + props.cname;
        /*選択肢ファイルを読み込んでoptions配列に代入*/


    }

    options = [
        "選択肢1",
        "選択肢2",
        "選択肢3"
    ];

    render(){
        return (
            <div className="options">
                <label　className="option">
                    <input type="radio" name="options" value="option1" /><span>{this.options[0]}</span>
                </label>
                <label className="option">
                    <input type="radio" name="options" value="option2" /><span>{this.options[1]}</span>
                </label>
                <label className="option">
                    <input type="radio" name="options" value="option3" /><span>{this.options[2]}</span>
                </label>
            </div>
        );
    }
}
export default Choice;
