import Link from "next/link";
import Layout from '../components/Layout';
import Question from '../static/Question';
import Choice from '../static/Choice';
import { Component } from "react";
import json_data from '../static/question_set.json'
import {connect} from 'react-redux';

class Main extends Component{

    constructor(props){
        super(props);
        this.question = json_data;
        this.handleChildFunc = this.handleChildFunc.bind(this)
        this.doAction = this.doAction.bind(this)
    }

    doAction(e){
        if(this.ans == this.question[this.props.i].answer){
            return this.props.dispatch({ type: 'CORRECT'});
        } else {
            return this.props.dispatch({ type: 'INCORRECT'});
        }
    }

    handleChildFunc(ans){
        if(ans == 1){
            console.log("1");
            this.ans = 1;
        } else if(ans == 2){
            console.log("2");
            this.ans = 2;
        } else if(ans == 3) {
            console.log("3");
            this.ans = 3;
        }
    }

    render(){
        return(
            <Layout>
                <div className="main-screen">
                   <div className="question_box">
                       <Question q_sentence={this.question[this.props.i].q_sentence} />
                   </div>
                   <div className="choice_box">
                       <Choice options={this.question[this.props.i].options} myFunc={this.handleChildFunc}/>
                   </div>
                    <div className="ans_btn" onClick={this.doAction}>回答</div>
               </div>
           </Layout>
        )
    }
}

Main = connect((state)=>state)(Main)
export default Main;
