import React, {Component} from 'react';
import {connect} from 'react-redux';

class Choice extends Component{

    constructor(props){
        super(props);
        this.myCheck = this.myCheck.bind(this);
    }

    myCheck(){
        for (const element of document.getElementsByName('option')) {
            element.checked = false;
          }
    }

    render(){
        return (
            <div className="options">
                <label　className="option" onClick={()=>this.props.dispatch({type: 'UPDATE1'})}>
                    <input type="radio" name="option" /><span>{this.props.question[this.props.q_no[this.props.i]].options[0]}</span>
                </label>
                <label className="option" onClick={()=>this.props.dispatch({type: 'UPDATE2'})}>
                    <input type="radio" name="option" /><span>{this.props.question[this.props.q_no[this.props.i]].options[1]}</span>
                </label>
                <label className="option" onClick={()=>this.props.dispatch({type: 'UPDATE3'})}>
                    <input type="radio" name="option" /><span>{this.props.question[this.props.q_no[this.props.i]].options[2]}</span>
                </label>
            </div>

        );
    }
}

Choice = connect((state)=>state)(Choice)
export default Choice;
