import Link from "next/link";
import Layout from '../components/Layout';
import Question from '../components/Question';
import Choice from '../components/Choice';
import Result from '../components/Result'
import { Component } from "react";
import {connect} from 'react-redux';
import { useRouter } from 'next/router'

class Main extends Component{

    constructor(props){
        super(props);
        this.doAction = this.doAction.bind(this);
        this.flg = true;
    }

    doAction(ans_no){
        //選択肢のチェックを外す
        for (const element of document.getElementsByName('option')) {
            element.checked = false;
          }
        //ストアに回答データを登録
        if(ans_no == this.props.question[this.props.q_no[this.props.i]].answer){
            this.props.dispatch({ type: 'CORRECT'});
        } else {
            this.props.dispatch({ type: 'INCORRECT'});
        }
        //最終問題の場合結果画面へ遷移
        if(this.props.i===9){
            this.flg = false;
        }
    }

    render(){
        return(
            <Layout>
                {this.flg ?
                    <div className="main-screen">
                        <div className="question_no_box">
                            <span className="question_no">第{this.props.i + 1}問</span>
                        </div>
                        <div className="question_box">
                            <Question />
                        </div>
                        <div className="choice_box">
                            <div className="options">
                                <label　className="option" onClick={this.doAction(1)}>
                                    <input type="radio" name="option" /><span>{this.props.question[this.props.q_no[this.props.i]].options[0]}<br/></span>
                                </label>
                                <label className="option" onClick={this.doAction(2)}>
                                    <input type="radio" name="option" /><span>{this.props.question[this.props.q_no[this.props.i]].options[1]}<br/></span>
                                </label>
                                <label className="option" onClick={this.doAction(3)}>
                                    <input type="radio" name="option" /><span>{this.props.question[this.props.q_no[this.props.i]].options[2]}<br/></span>
                                </label>
                            </div>
                        </div>
                    <div className="ans_btn" onClick={this.doAction}>回答</div>
                    </div>
                :
               <Result /> }
           </Layout>
        )
    }
}

Main = connect((state)=>state)(Main)
export default Main;
