import { createStore, applyMiddleware} from 'redux';
import thunkMiddleware from 'redux-thunk';
import json_data from './static/question_set.json'

//ステート初期化
const result = {
    q_no : [],
    question : json_data,
    i : 0,
    correct: 0,
    incorrect: 0,
    select: 0
}

//レデューサ
function counterReducer (state = result, action){
    switch (action.type){
        case 'SETRAND':
            return{
                q_no : [0, 1, 2, 3, 4],
                question : state.question,
                i : state.i,
                correct : state.correct,
                incorrect : state.incorrect,
                select: state.select
            };
        case 'CORRECT':
            return{
                q_no : state.q_no,
                question : state.question,
                i : state.i + 1,
                correct : state.correct + 1,
                incorrect : state.incorrect,
                select: 0
            };
        case 'INCORRECT':
            return{
                q_no : state.q_no,
                question : state.question,
                i : state.i + 1,
                correct : state.correct,
                incorrect : state.incorrect + 1,
                select: 0
            };
        case 'UPDATE1':
            return{
                q_no : state.q_no,
                question : state.question,
                i : state.i,
                correct : state.correct,
                incorrect : state.incorrect,
                select: 1
            };
        case 'UPDATE2':
            return{
                q_no : state.q_no,
                question : state.question,
                i : state.i,
                correct : state.correct,
                incorrect : state.incorrect,
                select: 2
            };    
        case 'UPDATE3':
            return{
                q_no : state.q_no,
                question : state.question,
                i : state.i,
                correct : state.correct,
                incorrect : state.incorrect,
                select: 3
            }; 
        default:
            return state;
    }
}

//initStore関数
export function initStore(state=result){
    return createStore(counterReducer, state, applyMiddleware(thunkMiddleware))
}
